import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Image } from 'react-native';
import { MessageCircle, UserPlus, Mars, Venus } from 'lucide-react-native';

interface User {
  id: string;
  fullName: string;
  handle: string;
  age: number;
  gender: string;
  country: string;
  city: string;
  bio: string;
  profilePicUrl: string;
  onlineStatus: boolean;
}

interface UserCardProps {
  user: User;
  onChatPress: () => void;
  onProfilePress: () => void;
  onFriendPress: () => void;
}

export function UserCard({ user, onChatPress, onProfilePress, onFriendPress }: UserCardProps) {
  const getCountryFlag = (country: string) => {
    const flags: { [key: string]: string } = {
      USA: 'üá∫üá∏',
      Canada: 'üá®üá¶',
      UK: 'üá¨üáß',
      Australia: 'üá¶üá∫',
      Germany: 'üá©üá™',
      France: 'üá´üá∑',
      Japan: 'üáØüáµ',
      Brazil: 'üáßüá∑',
    };
    return flags[country] || 'üåç';
  };

  return (
    <TouchableOpacity style={styles.card} onPress={onProfilePress}>

      {/* TOP IMAGE AREA */}
      <View style={styles.imageContainer}>
        <Image source={{ uri: user.profilePicUrl }} style={styles.profileImage} />

        {/* Status Badge */}
        <View style={styles.statusBadge}>
          <View
            style={[
              styles.statusDot,
              user.onlineStatus ? styles.online : styles.offline,
            ]}
          />
          <Text style={styles.statusText}>
            {user.onlineStatus ? 'Online' : 'Offline'}
          </Text>
        </View>
      </View>

      {/* CONTENT AREA */}
      <View style={styles.content}>
        {/* Name + Gender */}
        <View style={styles.nameRow}>
          <Text style={styles.name} numberOfLines={1}>
            {user.fullName}
          </Text>
          {user.gender === 'Male' ? (
            <Mars color="#767676" size={14} />
          ) : (
            <Venus color="#767676" size={14} />
          )}
        </View>

        <Text style={styles.handle}>{user.handle}</Text>

        {/* Age + Country */}
        <View style={styles.locationRow}>
          <Text style={styles.age}>
            {user.age} ‚Ä¢ {getCountryFlag(user.country)}
          </Text>
        </View>

        {/* Bio */}
        <Text style={styles.bio} numberOfLines={1}>
          {user.bio}
        </Text>

        {/* Buttons */}
        <View style={styles.buttonRow}>
          <TouchableOpacity style={styles.friendButton} onPress={onFriendPress}>
            <Text style={styles.friendButtonText}>Friend</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.chatButton} onPress={onChatPress}>
            <Text style={styles.chatButtonText}>Chat</Text>
          </TouchableOpacity>
        </View>
      </View>

    </TouchableOpacity>
  );
}

